/* Welcome to our Playground! */

p {
  color: blue;
}

/* Welcome to our Playground! */

h1 {
  text-align: center;
  color: darkblue;
}

/* Welcome to our Playground! */

h1 {
  color: red;
}

/* Welcome to our Playground! */

a:link {
  color: red;
}

a:visited {
  color: green;
}

/* Welcome to our Playground! */

p {
  color: blue;
}

.red {
  color: red;
}

#green {
  color: green;
}

/*
  Even though this might seem specific,
  it is still less specific than an ID!
*/
div > p.red {
  color: black;
}

/* Welcome to our Playground! */

:root {
  font-size: 18px;
}

#div1 {
  /* Has no effect, since the paragraph uses px */
  font-size: 18px;
}

#div1 p {
  font-size: 16px; /* 16px */
}

#div2 {
  font-size: 24px;
}

#div2 p {
  font-size: 1.5em; /* 36px */
}

#div3 {
  /* Has no effect, since the paragraph uses rem */
  font-size: 24px;
}

#div3 p {
  font-size: 1.5rem; /* 27px */
}

/* Welcome to our Playground! */

p {
  border: 2px solid black;
}

em {
  border: 2px solid blue;
}

/* Welcome to our Playground! */

p {
  /*
   Note paragraphs have a default margin, so a value of 0
   would be needed to completely remove this margin.
  */
  margin: 24px;
  padding: 10px;
  border: 2px solid black;
  background-color: lightblue;
}

/* Welcome to our Playground! */

.container {
  width: 50vw;
  background-color: lightblue;
}

.child {
  width: 100%;
  border: 10px solid black;
  padding: 0 8px;
  background-color: orange;
}

.border-box {
  box-sizing: border-box;
}

.content-box {
  box-sizing: content-box;
}

/* Welcome to our Playground! */

div {
  height: 50vh;
  margin: 24px;
  background-color: lightgrey;
  position: relative;
}

p {
  position: absolute;
  top: 10px;
  left: 10px;
}

/* Welcome to our Playground! */

#red {
  z-index: 1;
  background-color: red;
}

#blue {
  z-index: 2;
  top: 30px;
  left: 150px;
  background-color: blue;
}

#green {
  z-index: 3;
  top: 100px;
  left: 40px;
  background-color: green;
}

div {
  position: fixed;
  width: 40vw;
  height: 125px;
  border: 5px solid black;
  padding: 0.5rem;
  color: white;
  font-size: 2rem;
  font-weight: bold;
}

/* Welcome to our Playground! */

section {
  height: 90vh;
  border: 2px dotted black;
}

.red {
  height: 25px;
  background-color: red;
}

.green {
  height: 40px;
  background-color: green;
}

.blue {
  height: 30px;
  background-color: blue;
}

div {
  border: 5px solid black;
  padding: 0.5rem;
  color: white;
  font-size: 1.5rem;
  font-weight: bold;
}

/* Welcome to our Playground! */

body {
  display: grid;
}

body > * {
  border: 5px solid lightblue;
}

/* Welcome to our Playground! */

div {
  height: 75vh;
  color: white;
  background-image: url("https://assets.algoexpert.io/prod/dist/ae-social-logo.png");
  background-size: 100%;
}

/* Welcome to our Playground! */

div {
  color: red;
}

/* Welcome to our Playground! */

p {
  font-size: 24px;
  color: blue;
}

@media (max-width: 600px) {
  p {
    color: red;
  }
}

/* Welcome to our Playground! */

div {
  background-color: lightblue;
  width: 30%;
  height: 6em;
  border-radius: 12px;
  padding: 12px;
  margin: 4em;
  transition-property: width;
  transition-duration: 1s;
}

div:hover {
  width: 50%;
}

/* Welcome to our Playground! */

:root {
  --main-color: #00334C;
}

h1 {
  color: var(--main-color);
}

h2 {
  padding: 12px;
  color: white;
}

main {
  display: flex;
  border: 5px solid black;
  gap: 12px;
  width: 412px;
}

section {
  flex-basis: 200px;
  flex-shrink: 0;
  flex-grow: 0;
  background-color: var(--main-color);
}

/* Welcome to our Playground! */

.message {
  padding: 12px;
  border: 2px solid black;
  border-radius: 12px;
  font-size: 18px;
}

.message--success {
  color: white;
  background-color: green;
}
